const HorizontalScrollbar =({data,bodyPart,setBodyPart,isBodyParts})=>{
    return(
        <ScrollMenu>
            {data.map((item)=>(
                <Box
                key={item.id||item}
                itemId={item.id||item} 
                title={item.id||item}>
                    {isBodyParts?<BodyPart item={item} bodyPart={bodyPart} setBodyPart={setBodyPart}/>:<ExerciseCard exercise={item}/>}
                </Box>
            ))}
        </ScrollMenu>
    )
}

export default HorizontalScrollbar;
