import React from "react";
import "./style.css";

const Menucard =({menuData})=>{
    return(
        <>
        <section className="section1">
             {menuData.map((curElem)=>{
                const {id,name,category,description} =curElem;
                return(
                    <>
                <div className="container" key="curElem.id">
                <div className="card">
                <div className="card-body">
                <span className="card-number card-circle">{id}</span>
                <span className="card-author">{category}</span>
                <h2 className="card-title card-len">{name}</h2>
                <span className="card-description">
                    {description}
                </span>
                <hr className="hori"/>
                <div className="card-read">Read-More</div>
            </div>
            <span className="card-tag">Order Now</span>
        </div>
        </div>
        </>
     );
    })}       
    </section>  
        </> 
    )
}
 
export default Menucard;
